package algo.arrays_strings;

public class IndexOfString {

    public static void main(String[] args) {
        String haystack = "";
        String needle = "";
        String[] haystacks = {"hello", "aaaaa", "", haystack};
        String[] needles = {"ll", "bba", "", needle};
        int[] output = {2, -1, 0, 39999};
        for (int i=0; i<haystacks.length; i++) {
            System.out.println("strStr: " + (output[i] == strStr(haystacks[i], needles[i])));
            System.out.println("strStrUsingUtilityFunctions: " + (output[i] == strStrUsingUtilityFunctions(haystacks[i], needles[i])));
        }
    }

    public static int strStr(String haystack, String needle) {
        if (needle.length() == 0) {
            return 0;
        }
        boolean found;
        for (int i=0; i<=haystack.length()-needle.length(); i++) {
            if (haystack.charAt(i) == needle.charAt(0) && haystack.charAt(i+needle.length()-1) == needle.charAt(needle.length()-1)) {
                found = true;
                for (int j=1; j<needle.length(); j++) {
                    if (haystack.charAt(i+j) != needle.charAt(j)) {
                        found = false;
                        break;
                    }
                }
                if (found == true) {
                    return i;
                }
            }
        }
        return -1;
    }

    public static int strStrUsingUtilityFunctions(String haystack, String needle) {
        if (needle.length() == 0) {
            return 0;
        }
        for (int i=0; i<=haystack.length()-needle.length(); i++) {
            if (haystack.charAt(i) == needle.charAt(0) && haystack.substring(i, i+needle.length()).compareTo(needle) == 0) {
                return i;
            }
        }
        return -1;
    }

}